const Header = (props) => {
  return (
    <div>
      <p>{props.course}</p>
    </div>
  )
}

const Content = (props) => {
  return (
    <div>
      <p>{props.part1}</p>
      <p>{props.part2}</p>
      <p>{props.part3}</p>
    </div>
  )
}

const Total = (props) => {
  return (
    <div>
      <p>{props.exercises1} exercises</p>
      <p>{props.exercises2}</p>
      <p>{props.exercises3}</p>
    </div>
  )
}

const App = () => {
  const course = 'Half Stack application development'
  const part1 = 'Fundamentals of React'
  const exercises1 = 10
  const part2 = 'Using props to pass data'
  const exercises2 = 7
  const part3 = 'State of a component'
  const exercises3 = 14

  return (
    <div>
      <h1><Header course={course}/></h1>
      <p>
        <Content part1={part1}/> <Total exercises1={exercises1}/>
      </p>
      <p>
      <Content part1={part2}/> <Total exercises1={exercises2}/>
      </p>
      <p>
      <Content part1={part3}/> <Total exercises1={exercises3}/>
      </p>
      <p>Total number of exercises {exercises1 + exercises2 + exercises3}</p>
    </div>
  )
}

export default App
