import { useState } from 'react'

const Display = (props) => {
  return (
    <div>{props.counter}</div>
  )
}

const Button = (props) => {
  return (
    <button onClick={props.handleClick}>
      {props.text}
    </button>
  )
}

const App = () => {
  // tallenna napit omaan tilaansa
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  const Good = () => setGood(good + 1)
  const Neutral = () => setNeutral(neutral + 1)
  const Bad = () => setBad(bad + 1)


  return (
    <div>
      <h1>Give feedback</h1>

      <Button
        handleClick={Good}
        text='good'
      />
      <Button
        handleClick={Neutral}
        text='neutral'
      />
      <Button
        handleClick={Bad}
        text='bad'
      />
      <h1>statistics</h1>
      <p>Good: <Display counter={good}/></p>
      <p>Neutral: <Display counter={neutral}/></p>
      <p>Bad: <Display counter={bad}/></p>
      <p>All: <Display counter={good + neutral + bad}/></p>
    </div>
  )
}

export default App
